    print(file_name)
    
    # initialize nodes
    new_nodes = [word1, word2]
    
    # initialise edges
    new_edges = []
    
    # get neighbors for both words
    # select all neighbors or randomly select max 10
    neighbors = list(G.neighbors(word1))
    print(len(neighbors))
    if len(neighbors) <= 10:
        for elem in neighbors:
            new_edges.append((word1, elem))
    else:
        s = set()
        for i in range(10):
            idx = random.randint(0, len(neighbors)-1)
            if neighbors[idx] not in s:
                new_edges.append((word1, neighbors[idx]))
    
    neighbors = list(G.neighbors(word2))
    print(len(neighbors))
    if len(neighbors) <= 10:
        for elem in neighbors:
            new_edges.append((word2, elem))
    else:
        s = set()
        for i in range(10):
            idx = random.randint(0, len(neighbors)-1)
            if neighbors[idx] not in s:
                new_edges.append((word2, neighbors[idx]))
                
    # get shortest path
    try:
        shortest_path = nx.shortest_path(G, word1, word2)
    except:
        shortest_path = []
    
