def get_n_shortest_paths(G, n, source, target):
    path_to_len_arr = []
    try:
        all_paths = [p for p in nx.all_shortest_paths(G,source=source, target=target)]
    except:
        return "NA"
    for path in all_paths:
        path_to_len_arr.append((path, len(path)))
    
    path_to_len_arr.sort(key=lambda x: x[1], reverse=True)
    
#     for path in path_to_len_arr:
#         print(path)

    top_n = path_to_len_arr[:n]
    
    output = [x[0] for x in top_n]
    
    output_str = ""
    
    for path in output:
        curr_path = ""
        for keyword in path:
            curr_path += keyword + " => "
        output_str += curr_path[:(len(curr_path)-len(" => "))] + "|"
        
    # for path in output:
    #     print(path)
    return output_str

def remove_hubs(G):
    remove_hubs = ["software", "analysis", "machine", "application", "time", "science", "approach", "tool", "function", "method", "design", "project", "point", "introduction", "understanding"]
    for node in remove_hubs:
        try:
            G.remove_node(node)
        except:
            print("Already removed:", node)
    return G